import React from "react";


function BookDetails(props) {
    {props.book.volumeInfo.title &&
        <h3 className="book__item-title">
          {props.book.volumeInfo.title}
        </h3>
      }
      <div className="book__item-details">
        {props.book.volumeInfo.imageLinks.smallThumbnail &&
          <img src ={props.book.volumeInfo.imageLinks.smallThumbnail} alt={props.book.volumeInfo.title}/>
        }
        {props.book.volumeInfo.subtitle &&
          <h4><strong>Подзаголовок: </strong>{props.book.volumeInfo.subtitle}</h4>
        }
        {props.book.volumeInfo.description &&
          <p><strong>Описание: </strong>{props.book.volumeInfo.description}</p>
        }
        {props.book.volumeInfo.authors &&
          <p><strong>Авторы: </strong>{props.book.volumeInfo.authors}</p>
        }
        {props.book.accessInfo.webReaderLink &&
          <a href={props.book.accessInfo.webReaderLink}>Читать фрагмент</a>
        }
      </div>
}

export default BookDetails
